INSERT INTO `mentee` (`mentee_id`, `photo`, `full_name`, `phone`, `email`, `address`, `Country`, `State`, `employer`, `career_cat`, `yrs_exp`, `desc_exp`, `contact_meth`, `session_num`, `session_time`, `ref_1`, `ref_2`, `why_mentee`) VALUES
(1, 'http://dreamplanner.campuslifeohs.com/wp-content/uploads/2015/11/person-150x150.jpg', 'Eric Anderson', '801-319-8619', 'anderse7@oregonstate.edu', '442 Lakeview Dr.', 'USA', 'Utah', 'Starfleet Academy', 100, 1, 'I have flown a starship', 2, 2, 65, 'Hiroru Sulu', 'Pavel Checkov', 'I have no emotions'),
(2, 'http://dreamplanner.campuslifeohs.com/wp-content/uploads/2015/11/person-150x150.jpg', 'Jean Lc Picard', '333-444-5555', 'picardj@starfleet.org', 'Vinyard La Barre', 'France', 'Bourgogne', 'Starfleet Academy', 100, 1, 'How to pilot and command a starship', 1, 12, 30, 'Deanna Troy', 'William Riker', 'I\\''m just like James T. Kirk'),
(3, 'http://dreamplanner.campuslifeohs.com/wp-content/uploads/2015/11/person-150x150.jpg', 'William T. Ryker', '987-654-3210', 'rykerw@starfleet.org', '123 Valdez', 'USA', 'Alaska', 'Starfleet Command', 105, 1, 'I am number 1 at following', 3, 2, 47, 'Kyle Ryker', 'Betty Ryker', 'I am unorthodox in my approach to things');
INSERT INTO `mentor` (`id`, `photo`, `full_name`, `phone`, `address`, `state`, `employer`, `career_cat`, `yrs_exp`, `contact_meth`, `session_num`, `session_time`, `email`, `location`, `time_zone`, `desc_exp`, `ref_1`, `ref_2`, `why_mentor`) VALUES
(4, 'http://dreamplanner.campuslifeohs.com/wp-content/uploads/2015/11/person-150x150.jpg', 'James T. Kirk', '123-456-789', '123 Tumwah rd.', 'Iowa', 'Starfleet Command', 100, 15, 3, 7, 33, 'kirkjt@starfleet.org', 'USA', 'CST', '0', 'Chris Pyke', 'George Samuel Kirk', 'As a star fleet captain, I can lead a team under stress against the Kingons'),
(5, 'http://dreamplanner.campuslifeohs.com/wp-content/uploads/2015/11/person-150x150.jpg', 'Spock', '111-111-1111', 'Shi\\''Kahr, Vulcan', '', 'Starfleet Command', 101, 25, 2, 12, 45, 'spock@starfleet.org', 'USA', 'VTZ', '0', 'Amanda Grayson', 'Sarek', 'Spock carried a life-long interest in art, music, literature and poetry from many worlds, especially Terran, and played both tri-dimensional chess as well as the Vulcan lute or harp and a keyboard harpsichord.'),
(6, 'http://dreamplanner.campuslifeohs.com/wp-content/uploads/2015/11/Leonard-McCoy-150x150.jpg', 'Leonard McCoy', '333-333-3333', '123 Peachtree.', 'Georga', 'Starfleet Command', 103, 35, 1, 12, 30, 'mccoyl@starfleet.org', 'Georgia', 'EST', 'McCoy was born in Georgia, January 20, 2227. The son of David, he attended the University of Mississippi and is a divorce, but later married Natira, the priestess of Yonada, In 2266, McCoy was posted as chief medical officer of the USS Enterprise under Captain James T. Kirk who often calls him Bones.', 'Dr. Livingston', 'Jonas Sauk', 'I''m a doctor not an engineer'),
(7, 'http://dreamplanner.campuslifeohs.com/wp-content/uploads/2015/11/Uhura-150x150.jpg', 'Lt Uhura', '444-444-4444', 'Torama Village', '', 'Starfleet Command', 102, 10, 3, 6, 45, 'uhuralt@starfleet.org', 'Africa', 'UTC-1:00', 'Uhura, from the United States of Africa, speaks Swahili and was born January 19, 2233. Uhura first appears joining the crew of the USS Enterprise as a lieutenant, and serves as chief communications officer under Captain Kirk. She is a capable bridge officer and readily manned the helm, navigation and science stations on the bridge when the need arose. Uhura was also a talented singer, and enjoyed serenading her shipmates when off-duty.', 'Qintuquente', 'Alex Hayley', 'I can speak over 100 languages'),
(8, 'http://dreamplanner.campuslifeohs.com/wp-content/uploads/2015/11/Montgomery-Scott-150x150.jpg', 'Montgomery Scott', '555-555-5555', '486 Runnymeade', '', 'Starfleet Command', 105, 25, 2, 4, 15, 'scottm@starfleet.org', 'Scotland', 'UTC', 'Scotty spent part of his life in Aberdeen, Scotland, on March 3, 2222. Scotty holds the rank of lieutenant commander and serves as the Enterprise''s second officer and chief engineer. Scotty''s technical knowledge and skill allow him to devise unconventional and effective last-minute solutions to dire problems. As second officer, he commands the ship when both Captain Kirk and first officer Spock (Leonard Nimoy) are off the ship', 'Henry Ford', 'James Dodge', 'Cause I can fix anything no matter how much time'),
(9, 'http://dreamplanner.campuslifeohs.com/wp-content/uploads/2015/11/person-150x150.jpg', 'Pavel Checkov', '666-666-6666', '123 Lenin Square', '', 'Starfleet Command', 100, 10, 3, 6, 45, 'checkovp@starfleet.org', 'Russia', 'GMT+6', 'I fly ships for many years', 'James T. Kirk', 'Hikaru Sulu', 'I fly ship real fast and I like to learn'),
(10, 'http://dreamplanner.campuslifeohs.com/wp-content/uploads/2015/11/person-150x150.jpg', 'Joe Blow', '222-222-2222', '123 Any Street', '', 'OSU', 102, 1, 2, 3, 30, 'foo@bar.com', 'USA', 'PST', 'I like talking', 'charlie brown', 'linus Napp', 'I listen good');
